function rescan(){
    filesInProject = {};
    ambiguousFileNames = {};
    scanResults = _scan(baseDir, extensions);
    scanned = true;
    return scanResults;
}


function _scan (dir, extensions){
    var file, files, filePath, _i, _len;
    files = fs.readdirSync(dir);
    for (_i = 0, _len = files.length; _i < _len; _i++) {
        file = files[_i];
